sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    // Add your code here

    var rightNow = new GlideDateTime();
    var overDueNITasks = new GlideRecord("x_58872_needit_needit_task");
    overDueNITasks.addQuery("due_date", ">", rightNow);
    overDueNITasks.addQuery("due_date", "<=", gs.hoursAgo(-24));
    overDueNITasks.addQuery("assigned_to", current.assigned_to);
    overDueNITasks.query();

    template.print("You have this need it Task which is overdue in the next 24 hours - " + overDueNITasks.getRowCount() + " ");

	email.setSubject("Over Due NeedIt");
	email.addAddress("cc", current.assigned_to.manager.name);


})(current, template, email, email_action, event);
